import React from 'react';

import 'containers/dropdown/dropbox.css';

class Dropdown extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      isOpening : false
    };
    this.toggleOpening = this.toggleOpening.bind(this)
  }

  toggleOpening() {
    this.setState({
      isOpening : !this.state.isOpening
    });
  }

  render() {
    let downBox;
    if(this.state.isOpening) {
      downBox = 'I am opening!!!'
    }
    return (
      <div
          onClick={this.toggleOpening}
          className='dropbox'
      >
        Its Dropdown baby
        {downBox}
      </div>
    )
  }
}

export default Dropdown;