import React from 'react';

import 'containers/menu/menu.css';

class Menu extends React.Component {
  render() {
    const items = this.props.items.map( (item, key) => {
      return (
        <a
            href={item.link}
            key={key}
            className='menu__link'
        >
          {item.label}
        </a>
      )
    } );
    return (
      <div>
        {items}
      </div>
    )
  }
}

export default Menu