import React from 'react';

class Menu extends React.Component {
  render() {
    const items = this.props.items.map( (item, key) => {
      return (
        <a href={item.link} key={key}>
          {item.label}
        </a>
      )
    } );
    return (
      <div>
        {items}
      </div>
    )
  }
}

export default Menu